import React from 'react';
import { ChevronRightIcon } from '@heroicons/react/24/outline';

const Services: React.FC = () => {
  const services = [
    "Legal Proceedings & Disputes",
    "Succession Planning",
    "Exit Strategy",
    "Tax Purposes",
    "Mergers & Acquisitions",
    "Family Law Matters",
    "Insolvency & Bankruptcy",
    "Insurance Valuations",
    "Financial Reporting",
    "Expert Witness Services"
  ];

  return (
    <div className="py-16">
      <div className="container">
        <h1 className="text-4xl font-bold text-center mb-12">Our Services</h1>

        <div className="max-w-4xl mx-auto">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            {services.map((service, index) => (
              <div key={index} className="flex items-center p-4 bg-white rounded-lg shadow-sm hover:shadow-md transition-shadow">
                <ChevronRightIcon className="w-6 h-6 text-primary mr-4" />
                <span className="text-lg">{service}</span>
              </div>
            ))}
          </div>
        </div>

        {/* Service Description */}
        <div className="mt-16 max-w-3xl mx-auto text-center">
          <h2 className="text-2xl font-semibold mb-6">Comprehensive Business Valuation Services</h2>
          <p className="text-gray-600 mb-8">
            Whether you need a valuation for legal proceedings, succession planning, or any other purpose,
            our team of expert valuers provides accurate, reliable, and defensible business valuations
            that meet your specific needs.
          </p>
          <a href="/contact" className="btn btn-primary">
            Get Started
          </a>
        </div>
      </div>
    </div>
  );
};

export default Services;
